--> DROP
DROP TABLE PRODUCAO_ETANOL CASCADE CONSTRAINTS;
--> CREATE TABLE
CREATE TABLE PRODUCAO_ETANOL (
  REGIAO VARCHAR2(50),
  UNIDADE_FEDERACAO VARCHAR2(50),
  PRODUCAO_ETANOL NUMBER(10, 2),
  ANO NUMBER(4)
);
--> INSERT

------------QUESTAO 1 -----------
SELECT regiao AS "REGI츒"
FROM PRODUCAO_ETANOL
ORDER BY regiao ASC;


SELECT regiao AS "REGIAO", 
       SUM(PRODUCAO_ETANOL) AS "PRODUCAO DE ETANOL POR REGIAO"
FROM PRODUCAO_ETANOL
GROUP BY REGIAO
ORDER BY REGIAO ASC;

------------QUESTAO 2------------
SELECT "UNIDADE_FEDERACAO" AS "UNIDADE FEDERA츒",
       SUM("PRODUCAO_ETANOL") AS "PRODU츒 DE ETANOL POR UF"
FROM PRODUCAO_ETANOL
GROUP BY "UNIDADE_FEDERACAO"
ORDER BY "UNIDADE_FEDERACAO" ASC;

-------------QUESTAO 3 AGRUPADA-----
SELECT "REGIAO" AS "REGI츒",
       SUM("PRODUCAO_ETANOL") AS "PRODU츒 TOTAL DE ETANOL",
       SUM("PRODUCAO_ETANOL") AS "PRODU츒 TOTAL DE ETANOL PRODUZIDO POR UF",
       MIN("PRODUCAO_ETANOL") AS "MENOR PRODU츒 DE ETANOL PRODUZIDO POR UF",
       MAX("PRODUCAO_ETANOL") AS "MAIOR PRODU츒 DE ETANOL PRODUZIDO POR UF",
       ROUND(AVG("PRODUCAO_ETANOL"), 2) AS "PRODU츒 M텏IA DE ETANOL PRODUZIDO POR UF"
FROM PRODUCAO_ETANOL
GROUP BY "REGIAO"
ORDER BY "REGIAO" ASC;

------------QUESTAO 4------------
SELECT 
    REGIAO AS "REGI츒",
    COUNT(DISTINCT UNIDADE_FEDERACAO) AS "QTDE. UNIDADES DA FEDERA츒 POR REGI츒",
    SUM(PRODUCAO_ETANOL) AS "TOTAL DE PRODU츒 DE ETANOL",
    ROUND((SUM(PRODUCAO_ETANOL) / (SELECT SUM(PRODUCAO_ETANOL) FROM PRODUCAO_ETANOL) * 100), 2) AS "PERCENTUAL DA PRODU츒 POR REGI츒"
FROM 
    PRODUCAO_ETANOL
GROUP BY 
    REGIAO
ORDER BY 
    REGIAO ASC;

------------QUESTAO 5------------
SELECT
    ROWNUM AS "RANKING UF QUE MAIS PRODUZEM ETANOL",
    "UNIDADE FEDERA츒",
    "PRODU츒 TOTAL DE ETANOL POR UF"
FROM
    (SELECT
        UNIDADE_FEDERACAO AS "UNIDADE FEDERA츒",
        SUM(PRODUCAO_ETANOL) AS "PRODU츒 TOTAL DE ETANOL POR UF"
    FROM
        PRODUCAO_ETANOL
    GROUP BY
        UNIDADE_FEDERACAO
    ORDER BY
        "PRODU츒 TOTAL DE ETANOL POR UF" DESC)
WHERE ROWNUM<=10;

COMMIT;